import React from 'react';
import styled from 'styled-components';

const StyledThankYou = styled.div`
  text-align: center;
  padding: 20px;
  background-color: #f0f0f0;
  border-radius: 10px;
  box-shadow: 0 0 10px rgba(0, 0, 0, 0.2);
  max-width: 400px;
  margin: 0 auto;

  h1 {
    color: #333;
    font-size: 24px;
  }

  p {
    color: #555;
    font-size: 16px;
    margin: 10px 0;
  }
`;

const Success = () => {
  return (
    <div>
      <StyledThankYou>
      <h1>Thank You for Your Purchase!</h1>
